  textFont("Helvetica");
  text("click to play", width / 2, height / 2);
}

function draw() {
  penOsc.oscillator.type = selType.value();
  penOsc2.oscillator.type = selType.value();
  bassOsc.oscillator.type = selType.value();
  bassOsc2.oscillator.type = selType.value();
  if (clickedOnce) {
    background(20);
  }
  // console.log();
  n1 = floor(noise(frameCount * 0.001) * 3 + 8);
  if (floor(noise(frameCount * 0.01) * 90) % n1 == 0 && frameCount % 4 == 0) {
    penScale = shuffle(penScale);
    var penMidiValue = penScale[note];
    var penFreqValue = midiToFreq(penMidiValue);
    penOsc.freq(penFreqValue);
    penEnvelope.play(penOsc, 0, 0.1);
    note = (note + 1) % penScale.length;
    fill(0);
    // circle(10, 10, 10);
  }
  n2 = floor(noise(frameCount * 0.002) * 2 + 4);
  if (floor(noise(frameCount * 0.01) * 90) % n2 == 0 && frameCount % 40 == 0) {
    penScale2 = shuffle(penScale2);
    var penMidiValue2 = penScale2[note];
    var penFreqValue2 = midiToFreq(penMidiValue2);
    penOsc2.freq(penFreqValue2);
    penEnvelope2.play(penOsc2, 0, 0.1);
    note = (note + 1) % penScale2.length;
  }
  // console.log(frameCount % 800 > 200);
  if (frameCount > 3000) {
    if (frameCount % 1000 > 300) {
      n3 = floor(noise(frameCount * 0.02) * 4 + 10);
      if (
        floor(noise(frameCount * 0.01) * 92) % 2 == 0 &&
        frameCount % n2 == 0
      ) {
        bassScale = shuffle(bassScale);
        var bassMidiValue = bassScale[note];
        var bassFreqValue = midiToFreq(bassMidiValue);
        bassOsc.freq(bassFreqValue);
        bassEnvelope.play(bassOsc, 0, 0.1);
        note = (note + 1) % bassScale.length;
      }
    }
  }
  if (frameCount > 1000) {
    if (frameCount % 800 > 200) {
      n3 = floor(noise(frameCount * 0.02) * 4 + 5);
      if (
        floor(noise(frameCount * 0.01) * 8) % 3 == 0 &&
        frameCount % n3 == 0
      ) {
        bassScale2 = shuffle(bassScale2);
        var bassMidiValue2 = bassScale2[note];
        var bassFreqValue2 = midiToFreq(bassMidiValue2);
        bassOsc2.freq(bassFreqValue2);
        bassEnvelope2.play(bassOsc2, 0, 0.1);
        note = (note + 1) % bassScale2.length;
      }
    }
  }

  var spectrum = fft.analyze();
  // console.log(spectrum);
  for (var i = 0; i < spectrum.length / 8; i++) {
    stroke((1 / spectrum.length) * i * 10, 1, 0.8);
    var x = map(i, 0, spectrum.length / 10, 0, width);
    var h = map(spectrum[i], 0, 255, 0, height);
    if (i % 12 == 0) {
      rect(x, height, 1, -h);
    }
  }
}

