  clickedOnce = true;
  bassOsc.start();
  bassOsc2.start();
  penOsc.start();
  penOsc2.start();
  bassOsc.amp(0);
  bassOsc2.amp(0);
  penOsc.amp(0);
  penOsc2.amp(0);
}

