// Hold and drag mouse on canvas to draw shape. Release
// to get fill.

const canvasWidth = 600;
const canvasHeight = 600;
let cnv;

let dots = [];
let fillPoints = [];

function setup() {
    createCanvas(canvasWidth, canvasHeight);
    fill(254);
    pixelDensity(1);
    createP("1. Click, hold, draw a (small) shape.");
    createP("2. Release mouse and wait for the shape to fill.");
    createP("3. Draw more shapes, or refresh.");
}

function draw() {
    setPolygonDots();
    buildPolygon();
    // decoratePolygonPoints();
}

function setPolygonDots() {
    if (mouseIsPressed){
        polygonDrawActive = true;
        dots.push({x: mouseX, y: mouseY});
        ellipse(mouseX, mouseY, .5, .5);
    }
}

function buildPolygon() {
    beginShape();
    for (let dot = 0; dot < dots.length; dot++) {
        vertex(dots[dot].x, dots[dot].y);
    }
}

function fillPolygon() {
    loadPixels();
    for (let y = 0; y < height; y++) {
        for (let x = 0; x < width; x++) {
            let index = (x + y * width) * 4;
            let thisPixel = pixels[index];
            if (thisPixel === 254) {
                fillPoints.push([x, y]);
            }
        }
    }
}

function decoratePolygonPoints() {
    for (let i = 0; i < fillPoints.length; i++) {
        push();
        strokeWeight(.3);
        stroke(random(50, 60));
        line(
            fillPoints[i][0] - random(30),
            fillPoints[i][1] - random(30),
            fillPoints[i][0] + random(30),
            fillPoints[i][1] + random(30)
        );
        pop();
    }
}

function mouseReleased() {
    push();
    strokeWeight(0);
    endShape(CLOSE);
    fillPolygon();
    decoratePolygonPoints();
    pop();
    dots = [];
}

