// Pyramid tiles
// TODO: Add logic for more interesting tile combinations
//       e.g. rotations, edge matching, etc.
// TODO: Make Tile object methods cleaner

let debugging = false; // true turns on logging

// Canvas specs
const canvas_width = 750,
      canvas_height = canvas_width,
      num_cols = 15,
      num_rows = num_cols,
      tile_width = canvas_width / num_cols;

function setup() {
  // Set up canvas
  const canvas = createCanvas(canvas_width, canvas_height);
  canvas.parent("sketch-container");

  let rows = [];            // An array of row arrays, retrievable by row number
  let tilesOnThisRow = [];  // An array of Tile objects, retrievable by col number

  noStroke();
  for (row = 0; row < num_rows; row++) {
    for (col = 0; col < num_cols; col++) {
      tilesOnThisRow[col] = new Tile(col, row); // Add new tile to row
      tilesOnThisRow[col].drawSouth();
      tilesOnThisRow[col].drawWest();
      tilesOnThisRow[col].drawNorth();
      rows[row] = tilesOnThisRow; // Add row to array of rows
    }
  }
  if (debugging) { console.log(rows); }
}

class Tile {
  constructor(col, row) {
    this.x = col * tile_width;
    this.y = row * tile_width;
    this.rand_pt = {
      x: this.x + random(.8) * tile_width,
      y: this.y + random(.8) * tile_width
    };
  }

  drawSouth() {
    fill(0);
    triangle(
      this.x, this.y + tile_width,
      this.rand_pt.x, this.rand_pt.y,
      this.x + tile_width, this.y + tile_width
    );
    noFill();
  }

  drawEast() {
    fill(200, 200, 200);
    triangle(
      this.x + tile_width, this.y,
      this.rand_pt.x, this.rand_pt.y,
      this.x + tile_width, this.y + tile_width
    );
    noFill();
  }

  drawNorth() {
    fill(200, 200, 200);
    triangle(
      this.x, this.y,
      this.rand_pt.x, this.rand_pt.y,
      this.x + tile_width, this.y
    );
    noFill();
  }

  drawWest() {
    fill(100, 100, 100);
    triangle(
      this.x, this.y,
      this.rand_pt.x, this.rand_pt.y,
      this.x, this.y + tile_width
    );
    noFill();
  }
}

