function draw() {
  colorMode(HSB, 360, 1, 1, 1)
  background(250);
  noStroke()
  frameRate(30)
  // makeShape(700, width / 3, width / 3);
  let acc = sin(frameCount*0.01)
  let accMap = map(acc, -1, 1, -2*PI, 2*PI)
  translate(w/2, h/2)
  rotate(accMap)
  translate(-w/2, -h/2)
  let oscA = cos(frameCount*0.01)
  let mapOscA = map(oscA, -1, 1, 0, 360)
  let oscB = sin(frameCount*0.01)
  let mapOscB = map(oscB, -1, 1, 0, 360)
  makeCircles(140, w/2, h/2, mapOscA, mapOscB);
}

function makeCircles(num, x, y, startHue, endHue) {
  for (let i = 0; i < num; i += circleDistance) {
        makeCircle(i, x, y, startHue, endHue, num);
    }
}

function makeCircle(radius, xP, yP, startHue, endHue, num) {

  let circum = 2 * PI * radius
  for (let a = 0; a < circum/radius - circum/radius*0.1/num; a += PI/radius) {
    let x = radius * sin(a) + xP;
    let y = radius * cos(a) + yP;
    let hue = map(a, 0, 2*PI, startHue, endHue)
    fill(hue, 1, 1, 0.2)
    ellipse(x, y, circleSize, circleSize)
  }
}



