    myDistance = 601.1; // make sure position and diameter are not related
    myNoiseD = noise(
      i * noiseFrequency + sin(frameCount * 0.01) + frameCount * 0.001,
      myDistance
    );
    diameter = map(myNoiseD, 0, 1, 4, 20);

