    noFill();
    fill(0, 0, 1, 1 - myNoiseD);
    ellipse(x, y, diameter, diameter);
  }
}

var commands = '';
var turtle;
var step = 0;
var Iteration = 6;
var piter = Iteration;

function commandSetup() {
  var seq = 'F+++[F]+++[F]+++[F]';
  var rule = '[--f-F][fF][++f+F]';


  for (i = 0; i < Iteration; i++) {
    var newseq = '';
    for (j = 0; j < seq.length; j++) {
      if (seq[j] === 'F') {
        newseq += rule;
      } else {
        newseq += seq[j];
      }
    }
    seq = newseq;
  }
  commands = seq;
}

function setup() {
  angleMode(DEGREES);
  createCanvas(windowWidth, windowHeight);

  commandSetup();
  turtle = {

    turnangle: 20,
    len: 80,
    heading: 70,

    goto: function(x, y) {
      translate(x, y);
      rotate(this.heading);

    },

    obey: function(commands) {
      for (var i = 0; i < commands.length; i++) {
        var cmd = commands[i];

        switch (cmd) {

          case 'F':
            line(0, 0, 0, this.len);
            translate(20, this.len);
            break;

          case 'f':
            line(0, 0, 0, this.len);
            translate(10, this.len);
            break;

          case '+':
            rotate(this.turnangle);
            break;

          case '-':
            rotate(-this.turnangle);
            break;

          case '[':
            push();
            break;

          case ']':
            pop();
            break;
        }
      }
    }
  }

}

function draw() {
  if (Iteration != piter) {
    commandSetup();
  }

  //draw background
  blendMode(BLEND);
  background(0);
  fill(27, 28, 119, 4);
  noStroke();
  for (var r = 0; r < width; r+=8) {
    ellipse(windowWidth/2, windowHeight/2, r);
  }

  //draw turtle lines
  blendMode(ADD);
  noFill();
  stroke(107, 118, 0, 20);
  turtle.goto(width / 2, height / 2);
  turtle.obey(commands);
}

