const gotNotes = ['g4', 'c4', 'D4', 'f4', 'e4'];


function generate() {
    // generate some measures
    const a = generateMeasure();
    const b = generateMeasure();
    const c = generateMeasure();

    // first note should always be g
    a[0][0] = gotNotes[0];
    // last note should be fg
    c[c.length - 1][0] = gotNotes[4];
    // console.log(a);
    // console.log(b);
    // console.log(c);
    // arrange the measures and return
    return [].concat(a, b, c);
}

function generateMeasure() {
    const m = [];
    let timeLeft = Tone.Time('2m');

    while (timeLeft.toSeconds() > 0) {

        var note = randomNote();
        // console.log("random note " + note);
        var length = Tone.Time('2n');
        if (m[0] != null) {
            //if previous note was D or e, & it is not the end of the measure play an f
            if (m[m.length - 1][0] == "D4" || m[m.length - 1][0] == "e4") {
                //still need to add in part about end of measure 
                note = gotNotes[3];
                length = Tone.Time('8n');
                //fg pair always played for half note 

            }
            //if the previous note was an fg pair, play a c 
            else if (m[m.length - 1][0] == "g4") {
                note = gotNotes[1];
                //c always played for half note 
            } else if (m[m.length - 1][0] == "f4") {
                note = gotNotes[0];
                //g always played for half note 
            }
            // if the previous note was c, you must play a D or an e 
            else if (m[m.length - 1][0] == "c4") {
                // choose length
                note = "D4";
                if (Math.random() < 0.5) {
                    note = "e4";
                }

                length = Tone.Time('4n')
            }
        }
        // console.log("actual note " + note);
        if (length.toMilliseconds() > timeLeft.toMilliseconds()) {
            length = timeLeft;
        }

        // keep track of time
        timeLeft = Tone.Time(timeLeft - length);

        // add the note to the melody
        m.push([note, length]);

    }
    return m;
}

function play(melody) {
    let t = Tone.now();
    for (const note of melody) {
        console.log(note[0], note[1].toNotation());
        if (note[0] !== 'rest') {
            // synth.triggerAttackRelease(note[0], note[1]), t);
            synth.triggerAttackRelease(note[0], Tone.Time(note[1]) - 0.01, t);
        }
        t += Tone.Time(note[1]);
    }
}

