        if (myTurtle.x < width && myTurtle.y > 0) {
            if (myTurtle.y < height && myTurtle.y > 0) {
                myTurtle.moveForward(stepDist);
                myTurtle.turnRight(thisTurn);
            }
        }
        if (thisTurn === 180) {
            push();
            noStroke();
            // fill(75, 0, 130, 80);
            ellipse(myTurtle.x, myTurtle.y, stepDist, stepDist);
            pop();
        }
    }

	noLoop();
}

