//the scene is twice the size of the canvas
var SCENE_W = 1600;
var SCENE_H = 800;


function setup() {
  createCanvas(800, 400);

  //create a sprite and add the 3 animations
  dog = createSprite(400, 200, 50, 100);

  var myAnimation = dog.addAnimation('floating', 'assets/dog_walking-00.png', 'assets/dog_walking-07.png');
  myAnimation.offY = 18;

  dog.addAnimation('moving', 'assets/dog_walk0001.png', 'assets/dog_walk0004.png');

  bg = new Group();

  //create some background for visual reference
  for(var i=0; i<80; i++)
  {
    //create a sprite and add the 3 animations
    var bombs = createSprite(random(-width, SCENE_W+width), random(-height, SCENE_H+height));
    //cycles through bombs 0 1 2
    bombs.addAnimation('normal', 'assets/bombs'+i%3+'.png');
    bg.add(bombs);
  }

  frame = loadImage('assets/frame.png');
}

function draw() {
  background(255, 255, 255);

  //mouse trailer, the speed is inversely proportional to the mouse distance
  dog.velocity.x = (camera.mouseX-ghost.position.x)/20;
  dog.velocity.y = (camera.mouseY-ghost.position.y)/20;

  //a camera is created automatically at the beginning

  //.5 zoom is zooming out (50% of the normal size)
  if(mouseIsPressed)
    camera.zoom = 0.5;
  else
    camera.zoom = 1;

  //set the camera position to the dog position
  camera.position.x = dog.position.x;
  camera.position.y = dog.position.y;

  //limit the dog movements
  if(dog.position.x < 0)
    dog.position.x = 0;
  if(dog.position.y < 0)
    dog.position.y = 0;
  if(dog.position.x > SCENE_W)
    dog.position.x = SCENE_W;
  if(dog.position.y > SCENE_H)
    dog.position.y = SCENE_H;

  //draw the scene
  //bombs first
  drawSprites(bg);

  //shadow using p5 drawing
  noStroke();
  fill(0, 0, 0, 20);
  //shadow
  ellipse(dog.position.x, dog.position.y+90, 80, 30);
  //character on the top
  drawSprite(dog);

  //I can turn on and off the camera at any point to restore
  //the normal drawing coordinates, the frame will be drawn at
  //the absolute 0,0 (try to see what happens if you don't turn it off
  camera.off();
  image(frame, 0, 0);
}

