let synth;
let notes = ['A3', 'B2', 'C3', 'D2', 'E3', 'F3', 'F4','C3', 'D4', 'D3', 'B3', 'G4', 'E3', 'F4'];
let spots = [];
let stepSize = 50;
let cols = [];

let slider;
let start = false;
let freeverb;

function setup() {
	createCanvas(600, 600);
	freeverb = new Tone.Freeverb({
		roomSize: 0.5,
		dampening: 3000
	}).toMaster();
	synth = new Tone.PolySynth(4, Tone.Synth).connect(freeverb);

	background(255);
	noStroke();
	slider = createSlider(10, 200, 100);
	slider.position(200, 30);

	let cold = createP("cold");
	cold.position(150, 15);

	let hot = createP("hot");
	hot.position(350, 15);

	assignValues(slider.value());



}

function draw() {



	let sval = map(slider.value(), 10, 200, 0.9, 0.2);
	freeverb.roomSize.value = sval;

	translate(0, 40);
	for (var y = 0; y < height / stepSize; y++) {
		for (var x = 0; x < width / stepSize; x++) {
			//fill(0,100);

			if (mouseX > x * stepSize &&
				mouseX < x * stepSize + 50 &&
				mouseY > y * stepSize &&
				mouseY < y * stepSize + 50) {
				fill(0);
				play(spots[x][y].note);
				synth.triggerRelease(spots[x][y].note);
				start = false;

			} else
				fill(spots[x][y].colr);
			rect(x * stepSize, y * stepSize, 50, 50);

		}

	}

}




function mouseReleased() {
	if (mouseX > 200 & mouseX < 500 && mouseY > 10 && mouseY < 50) {
		assignValues(slider.value());
	}
}

function mouseMoved() {

	if (mouseY > 40 && mouseY < height) {
		start = true;
	} else
		start = false;
}

function play(notePick) {
	if (start) {
		synth.triggerAttackRelease(notePick, '2n')
	}
}



function getColorx(val) {

	var x = map(noise(xoff, yoff), 0, 1, val, 200);
	return x;
}

function getColory() {
	var y = map(noise(xoff, yoff), 0, 1, 50, 150);
	return y;
}

function assignValues(value) {

	for (let x = 0; x < width / stepSize; x++) {
		spots[x] = [];
		cols[x] = []; // create nested array
		for (let y = 0; y < height / stepSize; y++) {
			// let rand = Math.floor(random(0, 7));

			//cleanup-----------
			let rand;

			if (value < 100) {
				rand = Math.floor(map(getColorx(value), value, 100, 0, 6));
			} else if (value > 100) {
				rand = Math.floor(map(getColorx(value), value, 200, 6, notes.length));
				}else{
				rand = Math.floor(random(0, 7));
				}
			spots[x][y] = {
				colr: color(getColorx(value), getColory(), 100),
				//map it
				note: notes[rand]

			};

			xoff += 0.05;
		}
		yoff += 0.01;
	}



}

