// Used a great function to find the closest value in an array:
// https://stackoverflow.com/questions/8584902/get-closest-number-out-of-array
// Joe Grund & Richard Parnaby-King

var myImage;
let myHues = [];
let mySats = [];
let myLights = [];
let huesReduced = [];
let satsReduced = [];
let lightsReduced = [];

function preload() {
  myImage = loadImage(
    "http://res.cloudinary.com/compform2019/image/upload/c_fill,g_face,h_250,w_250/v1/avalanche2019/y1lmzaammdt4h6gebm6v"
  );
}

function setup() {
  pixelDensity(1);
  createCanvas(myImage.width * 2, myImage.height * 2);
  myImage.loadPixels();
  createP("Pixelisation");
  window.myPixels = createSlider(1, 5, 3, 1);
  createP("Number of hues");
  window.nHuesSlider = createSlider(1, 16, 4, 1);
  createP("Number of saturation values");
  window.nSatsSlider = createSlider(1, 16, 4, 1);
  createP("Number of lightness values");
  window.nLightsSlider = createSlider(1, 32, 20, 1);
  myUpdate(myPixels.value(), 4, 4, 21);
  createP("done?");
  button = createButton("update");
  button.mousePressed(myUpdate);
}

function myUpdate() {
  pixelisation = myPixels.value();
  nHues = nHuesSlider.value();
  nSats = nSatsSlider.value();
  nLights = nLightsSlider.value();
  storeHSL();
  huesReduced = reduceArray(myHues, nHues);
  satsReduced = reduceArray(mySats, nSats);
  lightsReduced = reduceArray(myLights, nLights);

  colorMode(HSL, 360, 100, 100, 1);
  for (var y = 0; y < myImage.height; y += pixelisation) {
    for (var x = 0; x < myImage.width; x += pixelisation) {
      position = y * myImage.width + x;
      var c = getQuick(myImage, x, y);
      myColor = color(
        getClosestValue(huesReduced, myHues[position]),
        getClosestValue(satsReduced, mySats[position]),
        getClosestValue(lightsReduced, myLights[position]),
        1
      );
      for (let xi = 0; xi < pixelisation; xi++) {
        for (let yi = 0; yi < pixelisation; yi++) {
          myImage.set(x + xi, y + yi, myColor);
        }
      }
    }
  }
  myImage.updatePixels();
  image(myImage, 0, 0, width, height);
}

function getQuick(img, x, y) {
  var i = (y * img.width + x) * 4;
  return [
    img.pixels[i],
    img.pixels[i + 1],
    img.pixels[i + 2],
    img.pixels[i + 3]
  ];
}

function storeHSL() {
  for (var y = 0; y < myImage.height; y++) {
    for (var x = 0; x < myImage.width; x++) {
      var c = getQuick(myImage, x, y);
      myHues.push(hue(color(c)));
      mySats.push(saturation(color(c)));
      myLights.push(lightness(color(c)));
    }
  }
}

// https://stackoverflow.com/questions/8584902/get-closest-number-out-of-array
function getClosestValue(array, goal) {
  var closest = array.reduce(function(prev, curr) {
    return Math.abs(curr - goal) < Math.abs(prev - goal) ? curr : prev;
  });
  return closest;
}

function reduceArray(array, nCategories) {
  arraySorted = array
    .slice(0)
    .filter(x => x != 0)
    .sort();
  arrayReduced = [];
  for (let i = 0; i < nCategories; i++) {
    index = floor(((arraySorted.length - 1) / nCategories) * i);
    arrayReduced.push(arraySorted[index]);
  }
  return arrayReduced;
}

function getArrAvg(array) {
  sum = array.reduce((a, b) => a + b);
  avg = sum / array.length;
  return avg;
}

