let lines = 10;
let line_closeness = 2.5;
let in_scale = 0.01;
let out_scale = 100;
let offset_sample_scale = 0.9;
let offset_scale = 1600;

function brush(x1, y1, x2, y2) {
  // draw line as series of 3 pixel line segments
  // this smooths out the curves
  let steps = dist(x1, y1, x2, y2) / 3;
  steps = max(steps, 1);

  for (let n = 0; n < steps; n++) {
    // find start/end of segment
    let start_x = lerp(x1, x2, n / steps);
    let start_y = lerp(y1, y2, n / steps);
    let end_x = lerp(x1, x2, (n + 1) / steps);
    let end_y = lerp(y1, y2, (n + 1) / steps);

    //draw brush
    strokeWeight(1);
    for (let i = 0; i < lines; i++) {
      noiseLine(
        start_x,
        start_y,
        end_x,
        end_y,
        (i * line_closeness) / lines,
        in_scale,
        out_scale
      );
    }
  }
}

function noiseLine(x1, y1, x2, y2, n, in_scale, out_scale) {
  x1 +=
    (noise(x1 * in_scale * millis() * 0.0001, y1 * in_scale, n) - 0.5) *
    out_scale;
  y1 +=
    (noise(x1 * in_scale * millis() * 0.0001, y1 * in_scale, n) - 0.5) *
    out_scale;

  x2 +=
    (noise(x2 * in_scale * millis() * 0.0001, y2 * in_scale, n) - 0.5) *
    out_scale;
  y2 +=
    (noise(x2 * in_scale * millis() * 0.0001, y2 * in_scale, n) - 0.5) *
    out_scale;

  line(x1, y1, x2, y2);
}

