const and = 'and';

const vowels = [
    'a',
    'e',
    'i',
    'o',
    'u'
];
const consonants = [
    'q', 'Q',
    'w', 'W',
    'r', 'R',
    't', 'T',
    'y', 'Y',
    'p', 'P',
    's', 'S',
