  stroke(80, 0, 0);
  makeShape2(700, width * 1.33 + n1, width * 1.33 + n2);
}

function makeRing(radius, xPar, yPar) {
  // Uses https://www.youtube.com/watch?v=76fiD5DvzeQ, adds noise

  beginShape();
  for (let a = 0; a < 360; a += 1) {
    let x = radius * sin(a) + xPar / 2;
    let y = radius * cos(a) + yPar / 2;
    let n = noise(
      x * 0.01 * mouseX * 0.0001 * (sin(millis() * 0.01) * 8000) * 0.001,
      y * 0.01 * mouseY * 0.002 + sin(millis() * 0.01) * 9000 * 0.001
    );
    vertex(x + radius * 0.3 * n, y + radius * 0.3 * n);
  }
  endShape(CLOSE);
}

function makeShape(numRings, x, y) {
  // noise(millis() * 0.001 + 30, millis() * 0.001 + 30);
  for (let i = 400; i < numRings; i += 4) {
    makeRing(i, x, y);
  }
}

function makeShape2(numRings, x, y) {
  // noise(millis() * 0.001 + 30, millis() * 0.001 + 30);
  for (let i = 350; i < numRings; i += 4) {
    makeRing2(i, x, y);
  }
}

function makeRing2(radius, xPar, yPar) {
  // Uses https://www.youtube.com/watch?v=76fiD5DvzeQ, adds noise

  beginShape();
  for (let a = 0; a < 360; a += 1) {
    let x = radius * sin(a) + xPar / 2;
    let y = radius * cos(a) + yPar / 2;
    let n = noise(
      x *
        0.01 *
        (width - mouseX) *
        0.00008 *
        (cos(millis() * 0.01) * 8000) *
        0.001,
      y * 0.01 * (height - mouseY) * 0.001 + cos(millis() * 0.01) * 9000 * 0.001
    );
    vertex(x + radius * 0.3 * n, y + radius * 0.3 * n);
  }
  endShape(CLOSE);
}

