            x + pixels_per_square, y,
            x + pixels_per_square, y + pixels_per_square
        );
    } else if (rand < .18 * 3) {
        // upper left triangle
        triangle(
            x, y,
            x + pixels_per_square, y,
            x, y + pixels_per_square
        );
    } else if (rand < .18 * 4) {
        // bottom right triangle
        triangle(
            x, y + pixels_per_square,
            x + pixels_per_square, y + pixels_per_square,
            x + pixels_per_square, y
        );
    } else if (rand < .18 * 5) {
        // bottom left triangle
        triangle(
            x, y + pixels_per_square,
            x + pixels_per_square, y + pixels_per_square,
            x + pixels_per_square, y
        );
    }
}

