function setup() {
  createCanvas(500, 300);

  createP('Amplitude');
  amplitude_slider = createSlider(0, 200, 120);

  createP('Frequency');
  frequency_slider = createSlider(0, .07, .2, .01);

  createP('Time Speed');
  speed_slider = createSlider(1, 3, .45);
}


function draw() {
  background(0);

  //sets gradient
  var color1 = color(230);
  var color2 = color(255);
  setGradient(0, 0, windowWidth, windowHeight, color1, color2, "Y");

  //sets gradient
  function setGradient(x, y, w, h, c1, c2, axis) {
    noFill();
    if (axis == "Y") { // Top to bottom gradient
      for (let i = y; i <= y + h; i++) {
        var inter = map(i, y, y + h, 0, 1);
        var c = lerpColor(c1, c2, inter);
        stroke(c);
        line(x, i, x + w, i);
      }
    } else if (axis == "X") { // Left to right gradient
      for (let j = x; j <= x + w; j++) {
        var inter2 = map(j, x, x + w, 0, 1);
        var d = lerpColor(c1, c2, inter2);
        stroke(d);
        line(j, y, j, y + h);
      }
    }

    ellipseMode(CENTER);
    noiseDetail(1, .02);
    fill(0);
    noStroke();

    let k;
    for (i = 0; i < 1; i += noiseFrequency) {
      var x = lerp(startX, endX, i);
      var y = lerp(startY, endY, i);
      k = noise(x * frequency_slider.value() + millis() * speed_slider.value() * noiseFrequency / 10);
      var offsetX = k * amplitude_slider.value();
      var offsetY = noise(x) * noiseFrequency;
      ellipse(x + offsetX, y + offsetY, 100, 10);
    }

  }
}

