module arc (arc_width, arc_height, arc_depth, include_arc) {
    union() {
        cube([arc_depth,,arc_width,arc_height],false);
        if (include_arc==true) translate([0,arc_width/2,arc_height]) rotate([0,90,0]) cylinder(h=arc_depth,, d=arc_width, center=false);
    }
}

// A building block

//block_width=12;
//block_height=12;
//block_depth=10;

module block (arc_width, block_width, block_height, block_depth) {
     cube([block_depth,block_width,block_height],false);
}


module arc_block (arc_width, arc_height, arc_depth, block_width, block_height, block_depth, include_arc, y_offset) {
    difference() {
        block(arc_width, block_width, block_height, block_depth);
        translate([0,0.5*(block_width-arc_width),y_offset]) translate([-0.25*arc_depth,0,-0.1]) arc(arc_width, arc_height, arc_depth, include_arc);
    }
}

//arc_block();

// first floor

//number_of_blocks=20;

module floor(arc_width, arc_height, arc_depth, block_width, block_height, block_depth, number_of_blocks, include_arc, y_offset) {
    for (r = [0:number_of_blocks]) {
        // arc block
//        translate([0,-0.5*number_of_blocks*block_width,0]) // center
        translate([0,block_width*r,0]) arc_block(arc_width, arc_height, arc_depth, block_width, block_height, block_depth, include_arc, y_offset);
        // arc stair
//        translate([0,-0.5*number_of_blocks*block_width,0]) // center
        translate([-3,block_width*r,0]) cube([7, block_width, 3], false);
    }
}

