class Hand {
    constructor(_x, _y, _img) {
        this.pos = {
            x: _x,
            y: _y,
        },
        this.img = _img,
        this.tip = {
            x: _x - .5,
            y: _y + 73
        },
        this.h = 800,
        this.w = .574 * this.h
    }

    lerpTo(_x, _y, _rand_mag, _penDown, _ptgroup) {
        this.penDown(_penDown, _ptgroup);
        this.display();

        let rand = {
            x: random(_rand_mag),
            y: random(- _rand_mag, _rand_mag)
        };

        const end_point_fixed = {
            x: _x + .5,
            y: _y - 73
        }

        this.pos.x = lerp(this.pos.x, end_point_fixed.x, 0.05) + rand.x;
        this.pos.y = lerp(this.pos.y, end_point_fixed.y, 0.05) + rand.y;

        this.tip = {
            x: this.pos.x - .5,
            y: this.pos.y + 73
        };
    }

    penDown(_isDown, _ptgroup) {
        // Points in line
        if (_isDown) {
            line_points[_ptgroup].push({x: this.tip.x, y: this.tip.y});
        }

        // Connect points
        strokeWeight(2.5);
        stroke(50);
        for (let i = 0; i < line_points.length; i++) {
            for (let p = 1; p < line_points[i].length; p++) {
                stroke(50);
                line(
                    line_points[i][p - 1].x, line_points[i][p - 1].y,
                    line_points[i][p].x, line_points[i][p].y,
                )
            }
        }
    }

    display() {
        image(this.img, this.pos.x, this.pos.y, this.w, this.h);
    }
}


function saveFrame(name, frameNumber, extension, maxFrame) {
    // don't save frames once we reach the max
    if (maxFrame && frameNumber > maxFrame) {
      return;
    }

    if (!extension) {
      extension = "png";
    }
    // remove the decimal part (just in case)
    frameNumber = floor(frameNumber);
    // zero-pad the number (e.g. 13 -> 0013);
    var paddedNumber = ("0000" + frameNumber).substr(-4, 4);

    save(name + "_" + paddedNumber + "." + extension);
  }

// saveFrame - a utility function to save the current frame out with a nicely formatted name
// format: name_####.extension
// name: prefix for file name
// frameNumber: number for the frame, will be zero padded
// extension: jpg or png, controls file name and image format
// maxFrame: checked against frameNumber, frames beyond maxFrame are not saved
function saveFrame(name, frameNumber, extension, maxFrame) {
    // don't save frames once we reach the max
    if (maxFrame && frameNumber > maxFrame) {
      return;
    }

    if (!extension) {
      extension = "png";
    }
    // remove the decimal part (just in case)
    frameNumber = floor(frameNumber);
    // zero-pad the number (e.g. 13 -> 0013);
    var paddedNumber = ("0000" + frameNumber).substr(-4, 4);

    save(name + "_" + paddedNumber + "." + extension);
  }

