noise.seed(Math.random());

var radiusStep = 4;
var count = 70;
var hueOffset = Math.random()*360;
var group = new Group();



function onFrame(event) {
group.removeChildren();


for(var i =0; i<count;i++){

  var path = new Path.Circle({
  	center: [500, 500],
  	radius: 35+i*radiusStep,
    strokeColor:'#f46060',
    strokeWidth:5 // red
  });
  path.strokeColor.hue+= (i*1.4 +hueOffset) ;
  path.flatten(i);
  path.smooth();
  //
  //
  for(j = 0; j< path.segments.length; j++){
    var dir = (path.segments[j].point.clone() - [500, 500]).normalize();
    var length = (noise.perlin3(i*0.1,j*1000,event.count*0.05) - 0.1*i*0.03) * i*i*0.07;
    path.segments[j].point += dir*length;
  }
  path.scale(1, 1+i*0.005);
group.addChild(path);
}

}

