function setPolygonDots() {
    if (mouseIsPressed){
        dots.push({x: mouseX, y: mouseY});
        ellipse(mouseX, mouseY, .5, .5);
    }
}

function buildPolygon() {
    beginShape();
    for (let dot = 0; dot < dots.length; dot++) {
        vertex(dots[dot].x, dots[dot].y);
    }
}

function fillPolygon() {
    loadPixels();
    for (let y = 0; y < height; y++) {
        for (let x = 0; x < width; x++) {
            let index = (x + y * width) * 4;
            let thisPixel = pixels[index];
            if (thisPixel === 254) {
                fillPoints.push([x, y]);
            }
        }
    }
}

function decoratePolygonPoints(c) {
    for (let i = 0; i < fillPoints.length; i++) {
        push();
        strokeWeight(.3);
        stroke(c[0], c[1], c[2]);
        line(
            fillPoints[i][0] - random(30),
            fillPoints[i][1] - random(30),
            fillPoints[i][0] + random(30),
            fillPoints[i][1] + random(30)
        );
        pop();
    }
}

function mouseReleased() {
    push();
    strokeWeight(0);
    endShape(CLOSE);
    fillPolygon();

    let c = [50, 50, 50];
    // uncomment this block for color
    // if (random() < .3) {
    //     c = [66, 134, 244];
    // } else if (random() < .6) {
    //     c = [156, 121, 158];
    // } else if (random() >= .6) {
    //     c = [79, 83, 132];
    // }
    decoratePolygonPoints(c);
    drawEyes();
    pop();
    dots = [];
}

