    for (var i = 0; i < 100; i++) {

        x = noise(i * noiseFrequency,0) * width;
        y = noise(i * noiseFrequency, 1000) * height;
        hv = random(huuue);
        h = noise(hv * noiseFrequency) * huuue
        s = 100;
        b = 100;
        fill(h, s, b);
        radius = map(h , 0, huuue, 3, 20);
        ellipse(x, y, radius, radius);
    }
    noLoop();
}

