var pitches = ["C3", "E3", "C3", "G3", "C2"];
// console.log(pitches.reverse());
function setup() {
  angleMode(DEGREES);
  ellipseMode(CENTER);

  cnv = createCanvas(500, 300);
  cnv.mouseClicked(mouseClickCanvas);

  createP("Frequency");
  frequency_slider = createSlider(0, 40, 2, 0.01);
  createP("Amplitude");
  amplitude_slider = createSlider(0, 10, 5, 0.1);
  createP("Time Speed");
  speed_slider = createSlider(0, 0.4, 0.05, 0.01);
  doWhilePause();
}

function draw() {
  if (play) {
    doWhilePlay();
  } else {
    doWhilePause();
  }
}

function doWhilePause() {
  for (let index = 0; index < myMonos.length; index++) {
    myMonos[index].triggerRelease();
  }
  background(255);
  push();
  fill(0);
  textSize(24);
  textStyle(BOLD);
  textFont("Helvetica");
  textAlign(CENTER);
  text("click to play", width / 2, height / 2);
  pop();
}

function doWhilePlay() {
  background(50);

  var amplitude = amplitude_slider.value();
  var frequency = frequency_slider.value();
  var speed = speed_slider.value();

  noiseDetail(1, 0.5);

  fill(255);
  noStroke();

  noisePosition += speed;
  // currentNotes = [];
  for (i = 0; i < 1; i += 1 / keys) {
    var x = lerp(endX, startX, i);
    var y = lerp(startY, endY, i);

    // var offsetX = noise(frequency * i + noisePosition, 0) * amplitude * 10;
    var offsetY = noise(frequency * i + noisePosition, 0) * amplitude * 10;
    var ballBottom = y + offsetY + 5;
    var keyInitialPos = y + 26;
    var keyIndex = floor(keys - 1 - i * (keys - 1));

    if (ballBottom >= keyInitialPos) {
      fill(255, 0, 0);
      keyPos = ballBottom;

      // currentNotes.push(keyIndex);
      // console.log(keyIndex);
      // mySynths[keyIndex].noteAttack(pitches[keyIndex], 0.1);
      myMonos[keyIndex].triggerAttack(pitches[keyIndex], 0.1);
    } else {
      keyPos = keyInitialPos;
      fill(255);
      myMonos[keyIndex].triggerRelease();
      // currentNotes.splice(currentNotes.indexOf(keyIndex), 1);
    }
    // console.log(currentNotes);
    ellipse(x, y + offsetY, 10, 10);
    // console.log(ballBottom, keyPos);
    push();
    stroke(255);
    line(x - 5, keyPos, x + 5, keyPos);
    pop();
  }
}

function mouseClickCanvas() {
  if (play) {
    play = false;
  } else {
    play = true;
  }
}

