// require https://cdnjs.cloudflare.com/ajax/libs/p5.js/0.7.3/p5.js
// code by Daniel Shiffman "3D Terrain Generation with Perlin Noise in Processing" https://www.youtube.com/watch?v=IKB1hWWedMk

var cols, rows;
var scallee = 8;

var h = 800;
var w = 800;

var flying = 0;
var terrain = [];

function setup() {
  createCanvas(500, 500, WEBGL);
  cols = w / scallee;
  rows = h / scallee;

  for (var x = 0; x < cols; x++) {
    terrain[x] = [];
    for (var y = 0; y < rows; y++) {
      terrain[x][y] = 0;
    }
  }
}

function draw() {
  background(240);
  flying -= 0.4;
  var yoff = flying;
  for (var y = 0; y < rows; y++) {
    var xoff = 0;
    for (var x = 0; x < cols; x++) {
      terrain[x][y] = map(noise(xoff, yoff), 0, 1, -150, 150);
      xoff += 0.02;
    }
    yoff += 0.02;
  }

  translate(0, 120);
  rotateX(PI/2.3);
  smooth();
  strokeWeight(0.18);
  stroke(0);
  translate(-w/2, -h*1.5);
  for (var y = 0; y < rows-1; y++) {
    beginShape(POINTS);
    for (var x = 0; x < cols; x++) {
      vertex(x*scallee, y*scallee, terrain[x][y]);
      vertex(x*scallee, (y+1)*scallee, terrain[x][y+1]);
    }
    endShape();
  }
}

