class Hand {
    constructor(_x, _y, _img) {
        this.pos = {
            x: _x,
            y: _y,
        },
        this.img = _img,
        this.tip = {
            x: _x - .5,
            y: _y + 141
        },
        this.w = 600,
        this.h = 600 / 1.2
    }

    moveBrownian(_step_dist) {
        let rand = {
            x: random(_step_dist),
            y: random(-_step_dist, _step_dist)
        };
        this.pos.x += rand.x;
        this.pos.y += rand.y / 6;

        this.tip.x += rand.x;
        this.tip.y += rand.y / 6;
    }

    // drawNoisyLine(_to, _from) {
    //     penDown();
    // }

    penDown(_color, _weight) {
        // Points in line
        line_points.push({x: this.tip.x, y: this.tip.y});

        // Connect points
        strokeWeight(2);
        stroke(50);
        // beginShape();
            for (let p = 1; p < line_points.length; p++) {
                // vertex(line_points[p].x, line_points[p].y);
                line(
                    line_points[p - 1].x, line_points[p - 1].y,
                    line_points[p].x, line_points[p].y,
                )
            }
        // endShape();
    }

    display() {
        image(this.img, this.pos.x, this.pos.y, this.w, this.h);
    }
}

