function create6thLevel(parents) {
    var allTriangles = [];
    for (var i = 0; i < parents.length; i++) {
        createNextGen(parents[i]);
    }
    return allTriangles;
}

function createNextGen(parents) {
    var allTriangles = [];
    for (var i = 0; i < parents.length; i++) {
        allTriangles[i] = createChildren(parents[i]);
    }
    return allTriangles;
}

function createChildren(parentT) {
    var children = [];
    children[0] = createChild(parentT, 'top');
    children[1] = createChild(parentT, 'right');
    children[2] = createChild(parentT, 'left');

    return children;
}


function createChild(parentT, direction) {
    var triangleInfo = [];
    //create an array 
    //each array is position 0, triangle object 
    //position 1 desired triangle path 
    //position 2 starting trinagle path 
    if (direction == "right") {
        var direction = new Point(parentT.center.x + parentT.sideLength / 2, parentT.center.y + parentT.height / 2);
    } else if (direction == "left") {
        var direction = new Point(parentT.center.x - parentT.sideLength / 2, parentT.center.y + parentT.height / 2);
    } else if (direction == "top") {
        var direction = new Point(parentT.center.x, parentT.center.y - (2.5 * parentT.height));
    }
    var triangle = {
        radius: parentT.radius / 2,
        center: direction,
        height: parentT.radius / 4,
        sideLength: parentT.radius / 2 * Math.sqrt(3),
    };
    triangleInfo[0] = triangle;
    var trianglePath = new Path.RegularPolygon(triangle.center, 3, triangle.radius);
    triangleInfo[1] = trianglePath;
    // trianglePath.strokeColor = "red";
    trianglePath.rotate(180);

    //traveling sequence 
    //triangle 1 start
    triangleInfo[2] = moveTriangle(triangle);

    return triangle;

}



function moveTriangle(triangle) {
    var start = new Point(1000, 800) * Point.random();
    //if in ther middle of the screen somewhere in range subtract certain numebr of random point or add certain number of random point 
    if (start.x > 100 && start.x < 500) {
        // new Point(300, 0);
        start -= new Point(250, 0);
    }

    if (start.x > 500 && start.x < 950) {
        start += new Point(250, 0);
    }
    var triangleMove = new Path.RegularPolygon(start, 3, triangle.radius);
    if (triangle != triangle1) {
        triangleMove.rotate(180);
    }
    // console.log(triangleMove);

    var Xtravel = triangle.center.x - start.x;
    var Ytravel = triangle.center.y - start.y;

    var travelIncriment = 100;
    var travelXPerFrame = Xtravel / travelIncriment;
    // console.log(travelXPerFrame);
    var travelYPerFrame = Ytravel / travelIncriment;
    var travelPerFrame = new Point(travelXPerFrame, travelYPerFrame)

    var opacityIncriment = .005;
    triangleMove.opacity = .001;
    var lightness = (Math.random() - 0.5) * 0.4 + 0.4;
    var hue = Math.random() * 360;
    triangleMove.fillColor = {
        hue: hue,
        saturation: 1,
        lightness: lightness
    };


    triangleMove.blendMode = 'multiply';

    triangleMove.onFrame = function (event) {
        if (event.count < travelIncriment) {

            this.position += travelPerFrame;
            // console.log(triangleMove.position);
            triangleMove.strokeColor = 'white';
            triangleMove.strokeWidth = .5;
            // triangleMove.fillColor = 'grey';
            triangleMove.opacity += opacityIncriment;


            this.rotate((360 / travelIncriment));
        }
    }
    return triangleMove;

}


