/* L-system turtle movement
 *
 * WARNING: MORE THAN 8 GENERATIONS WILL PROBABLY FREEZE
 * YOUR BROWSER
 *
 * alphabet: F+-[]
 * axiom: F
 * rules: F -> F+[-FF]+[+FF]
 *
 * References
 * - https://en.wikipedia.org/wiki/L-system#Example_1:_Algae
 * - https://www.youtube.com/watch?v=E1B4UoSQMFw
 * 
 * Unlike the referenced tutorial/reading, this code:
 * - 1) uses a custom rule set
 * - 2) uses the turtle.js library
 */

let step = 40;
let angle = 30;
const axiom = "F";
let sentence = axiom;

const rules = [];
rules[0] = {
    input: "F",
    // output: "FF+[+F-F-F]-[-F+F+F]" // standard algae L-system rule
    output: "F+[-FF]+[+FF]" // custom L-system rule
};

function setup() {
    createCanvas(windowWidth, windowHeight - 25);
    background(240);
    strokeWeight(.08);

    myTurtle = new Turtle();

    const generateButton = createButton("Generate");
    generateButton.mousePressed(generate);
    createP(axiom);

    myTurtle.turnLeft(90);
}

function generate() {
    // step *= 0.5;
    let outputSentence = "";
    for (let i = 0; i < sentence.length; i++) {
        let currentChar = sentence.charAt(i);

        let ruleFound = false; // default
        for (let j = 0; j < rules.length; j++) {
            if (currentChar === rules[j].input) {
                ruleFound = true;
                outputSentence += rules[j].output;
                break;
            }
        }
        if (!ruleFound) {
            outputSentence += currentChar;
        }
    }
    sentence = outputSentence;
    createP(outputSentence);
    moveTurtle();
}

function moveTurtle() {
    myTurtle.penUp();
    // myTurtle.moveTo(width / 2, height);
    // myTurtle.moveTo(windowWidth / 7, height);
    myTurtle.penDown();
    stroke(50);

    for (let i = 0; i < sentence.length; i++) {
        let currentChar = sentence.charAt(i);
        if (currentChar === "F") {
            // move forward
            myTurtle.moveForward(step);
        } else if (currentChar === "+") {
            // turn right
            myTurtle.turnRight(angle);
        } else if (currentChar === "-") {
            // turn left
            myTurtle.turnRight(-angle);
        } else if (currentChar === "[") {
            // push status
            myTurtle.pushState();
        } else if (currentChar === "]") {
            // pop status
            myTurtle.popState();
        }
    }
}

