// Code needs cleaning and parameterizing, but it functions.

const canvas = {
    w: 800,
    h: 800
};

function setup() {
    createCanvas(canvas.w, canvas.h);
    strokeWeight(.5);
    colorMode(HSB, 1);
    stroke(.7, .2, .3);
    noFill();
    angleMode(DEGREES);
}

function draw() {
    background(255);
    makeShape(Math.floor(canvas.w * .4));
}

function makeRing(radius) {
    beginShape();
    let numPoints = 1000;
    for(let i=0; i < numPoints; i++) {
        let raw = {
            x: (canvas.w / 2) + radius * Math.cos(i * (2 * Math.PI) / numPoints),
            y: (canvas.h / 2) + radius * Math.sin(i * (2 * Math.PI) / numPoints)
        };
        let noisyRadius = radius + 50 * noise(raw.x * .01, raw.y * .01, millis() * .001);
        let noisy = {
            x: (canvas.w / 2) + noisyRadius * Math.cos(i * (2 * Math.PI) / numPoints),
            y: (canvas.h / 2) + noisyRadius * Math.sin(i * (2 * Math.PI) / numPoints)
        };
        vertex(noisy.x, noisy.y);
    }
    endShape(CLOSE);
}

function makeShape(numRings) {
    for (let i = 0; i < numRings; i += 5) {
        makeRing(i);
    }
}

