var num = 300;
var xPositions = new Array(num);
var yPositions = new Array(num);
var r_slider, g_slider, a_slider;


function setup() {
  createCanvas(400, 400);
  noStroke();

  for (var i = 0; i < xPositions.length; i++) {
    xPositions[i] = random(width);
  }

  for (var i = 0; i < yPositions.length; i++) {
    yPositions[i] = map(i, 0, yPositions.length, height / 3, height * 4/ 5);
  }

  createP('RGBA R Value');
  r_slider = createSlider(0, 100, 50);

  createP('RGBA G Value');
  g_slider = createSlider(0, 100, 30);

  createP('RGBA A Value');
  a_slider = createSlider(0, 255, 200);
}

function draw() {
  background(75);
  var r = r_slider.value();
  var g = g_slider.value();
  var a = a_slider.value();

  for (var i = 0; i < xPositions.length; i++) {
    var siz = map(i, 0, xPositions.length, 200, 10);
    fill(r, g, (map(i, 0, xPositions.length, 10, 255)),a);
    ellipse(xPositions[i], yPositions[i], siz, siz);
  }


  for (var i = 0; i < xPositions.length; i++) {
    var siz = map(i, 0, xPositions.length, 200, 10);
    xPositions[i] -= map(i, 0, xPositions.length, 1, 20);

    if (xPositions[i] < -siz / 2) {
      xPositions[i] = width + siz / 2;
    }
  }

}

