// Uses patterns from Vera MolnÃ¡r's work to play with
// high, low, normal distributions in form of stroke weight.
// See line 35.

const canvas_width = 800,
    canvas_height = 900,
    step = 14,
    border_pixels = 20,
    max_rand = step / 10;

function setup() {
    createCanvas(canvas_width, canvas_height);
    stroke(165, 42, 42);
    noFill();
    background(255, 250, 240, 100);
    compose();
}

function compose() {
    for (y = border_pixels; y < canvas_height - border_pixels - step; y++) {
        for (x = border_pixels; x < canvas_width - border_pixels - step; x++) {
            if (x % step === 0 && y % step === 0) {
                setStrokeWeight();
                rect(x + random(max_rand), y + random(max_rand), 20, 20);
                drawInnerRect(8, 10);
                drawInnerRect(4, 12);
                drawInnerRect(2, 18);
            }
        }
    }
}

function setStrokeWeight() {
    // TOGGLE BETWEEN DIFFERENT STROKE WEIGHT DISTRIBUTIONS
    // strokeWeight(max(random(1), random(1), random(1), random(1))); // high
    // strokeWeight(min(random(1), random(1), random(1), random(1))); // low
    strokeWeight((random(1) + random(1) + random(1) + random(1)) / 4); // normal

    // MAKE SOME % OF SQUARES INVISIBLE, AS FUNCTION OF Y VALUE
    y_norm = map(y, border_pixels, canvas_height - border_pixels, 0, 1);
    if (random() < y_norm) {
        strokeWeight(0);
    }
}

function drawInnerRect(step_divider, step_adjuster) {
    rect(
        x + (step / step_divider) + random(-max_rand, max_rand), // x coordinate
        y + (step / step_divider) + random(-max_rand, max_rand), // y coordinate
        step - step_adjuster, // width
        step - step_adjuster // height
    );
}

