class Circle {
  constructor(_pos, _color, _diam, _note) {
    (this.pos = _pos),
    (this.color = _color),  // const
    (this.diam = _diam),
    (this.note = _note),    // const
    (this.start = _pos);    // const
  }

  updateSelf(_to, _speed) {
    this.pos.x = lerp(this.pos.x, _to.x, _speed);
    this.pos.y = lerp(this.pos.y, _to.y, _speed);
  }

  drawSelf() {
    push();
    fill(this.color);
    ellipse(this.pos.x, this.pos.y, this.diam, this.diam);
    pop();
  }
}

function draw() {
  background(240);
//   A3.amp(0.25, 0.05);
