    text(textContent, x + i * tracking, y + i * vert);
  }
}

// require https://cdnjs.cloudflare.com/ajax/libs/p5.js/0.5.14/p5.js

// This code builds off of Justin Bakse's Brownian Motion code
// in the http://compform.net/random/ course material.
// Specifically, *this* code:
// - creates several controlled, vertical brownian paths
// - isolates the each path's unpredictable movement to the x
// direction, and only active at the center of the canvas
//
// Overall look inspired by Georg Nees.

const canvasWidth = 800,
    canvasHeight = 800,
    halfCanvasWidth = canvasWidth / 2,
    halfCanvasHeight = canvasHeight / 2,
    numCols = 60,
    stepDistance = 5,
    numSteps = canvasHeight / stepDistance;

let xStart = 10,
    yStart = 10;

function setup() {
    createCanvas(canvasWidth, canvasHeight);
    noLoop(); 
}

function draw() {
    strokeWeight(.5);

    // Draw lots of vertical paths
    for (c = 0; c < numCols; c++) {
        drawPath(xStart, yStart, stepDistance, numSteps);
        xStart += (canvasWidth - 20) / numCols;
    }
}

function drawPath(x, y, segmentLength, steps) {
    for (var step = 0; step < steps; step++) {

        // Isolate the path's wiggle to the center of the canvas
        if (dist(x, y, halfCanvasWidth, halfCanvasHeight) < 100) {
            randomRangeValue = 4;
        } else {
            randomRangeValue = 0;
        }

        // Isolate the path's wiggle to the x-axis
        var nextX;
        nextX = xStart + random(-randomRangeValue, randomRangeValue);
        nextY = y + segmentLength;

        line(x, y, nextX, nextY);

        x = nextX;
        y = nextY;
    }
}

//require https://cdnjs.cloudflare.com/ajax/libs/p5.js/0.7.3/p5.min.js
 function setup() {
    createCanvas(600, 600);
    background(0, 0, 0);
    smooth();
    // colorMode(HSB);
    colorMode(RGB, 255, 255, 255, 1);


    // startShape();
    noFill();
    stroke(255, 102, 0, 1);

    //a tuft of hair 
    for (tuft = 1; tuft < 15; tuft++) {
        //starting point for a tuft of hair 
        origin = {
            x: tuft * 50,
            y: 50,
        };
        console.log(origin.x);
        //end point for a tuft of hair 
        strandEnd = {
            x: tuft * 50,
            y: 400,
        };
        //curve deviation relative to strands 
        let curveDeviation = (Math.random() * 150) + 50;

        //strands in a tuft
        for (strand = 0; strand < 200; strand++) {
            //using same color and opacity randomness form previous day 
            let red = Math.floor(100 * Math.random() + 155);
            let green = Math.floor(20 * Math.random() + 50);
            let opacity = Math.random() - .5;
            stroke(red, green, 10, opacity);


            //origin for a strand of hair deviates slightly from central tuft position
            let originDeviationX = (Math.random() * 30) + 5;
            curveStart = {
                x: origin.x + originDeviationX,
                y: origin.y,
            };

            //end point of each strand
            let strandEndDeviationX = (Math.random() * 20) + 5;
            //make strands that fall further from the x axis longer, and strands that end closer, shorter
            let length = map(strandEndDeviationX, 5, 25, 0, 200);
            curveEnd = {
                x: strandEnd.x + strandEndDeviationX,
                y: strandEnd.y + length,
            };

            //straight axis for start and end of strand of hair
            let axis = dist(curveStart.x, curveStart.y, curveEnd.x, curveEnd.y);
            //halfway point of a strand 
            let halfAxisLength = curveEnd.y / 2;



            // control points 
            //the amount of curvature  of a control point is relative to its origin 

            let controlOne = {
                //let the x axis of the control point be a random no larger than the curvedeviation
                x: curveStart.x - (Math.floor(Math.random() * curveDeviation) + 100),
                y: Math.floor(Math.random() * halfAxisLength),
            };

            //control Pont Two
            let controlTwo = {
                x: curveStart.x + (Math.floor(Math.random() * curveDeviation)),
                y: Math.floor((Math.random() * halfAxisLength + halfAxisLength)),
            };

            bezier(curveStart.x, curveStart.y, controlOne.x, controlOne.y, controlTwo.x, controlTwo.y, curveEnd.x, curveEnd.y);
        }
    }
}

