function drawBranch(length) {

	if (length < 2) {
		return;
	}

	// draw this branch
	myTurtle.moveForward(length);

	// left child
	myTurtle.pushState();
	myTurtle.turnLeft(80);
	drawBranch(length * 0.70);
	//drawBranch(length * random(.5, .9));

	myTurtle.popState();

	// right child
	myTurtle.pushState();
	myTurtle.turnRight(35);
	drawBranch(length * 0.75);
	//drawBranch(length * random(.5, .9));
    stroke(255,255,255,90);
	strokeWeight(4);
    strokeCap(ROUND)
	myTurtle.popState();

}

