function parseResult() {
    // recognition system will often append words into phrases.
    // so hack here is to only use the last word:
    const s = myRec.resultString;
    let sNorm = '';
    console.log(s);
    if (s.match(/treat|cheat|eat/g)) {
        sNorm = 'treat';
    } else if (s.match(/walk/g)) {
        sNorm = 'walk';
    } else if (s.match(/squirrel/g)) {
        sNorm = 'squirrel';
    } else if (s.match(/bath/g)) {
        sNorm = 'bath';
    }
    voiceInput = sNorm;
}

