// require https://cdnjs.cloudflare.com/ajax/libs/p5.js/0.5.14/p5.js

// This code builds off of Justin Bakse's Brownian Motion code
// in the http://compform.net/random/ course material.
// Specifically, *this* code:
// - adds repetition to the existing draw pattern
// - adds random variability to the existing draw pattern, in the form
//   of step distance (ie density of overall form)
// - makes the brightness of the line drawn a function of the distance
//   it has traveled from its origin point.

const canvasWidth = 800,
    canvasHeight = 800,
    halfCanvasWidth = canvasWidth / 2,
    halfCanvasHeight = canvasHeight / 2;

function setup() {
    createCanvas(canvasWidth, canvasHeight);
    ellipse(canvasWidth * .5, halfCanvasHeight, 30, 30);
    noLoop();
    strokeWeight(.5);
    colorMode(HSB, 1);   
}

function draw() {
    let x = halfCanvasWidth;
    let y = halfCanvasHeight;

    // draw 30 brownian paths over each other, at variable densities,
    // each with 3k steps
    for (n = 0; n < 30; n++) {
        drawBrownianPath(halfCanvasWidth, halfCanvasHeight, random(3, 4), 3000);
    }
}

function drawBrownianPath(x, y, segmentLength, steps) {
    for (var step = 0; step < steps; step++) {

        distanceFromCenter = dist(x, y, halfCanvasWidth, halfCanvasHeight);
        maxDistance = sqrt(sq(canvasWidth) + sq(canvasHeight));
        radius = 50;

        lineBrightness = map(distanceFromCenter, 0, maxDistance, 0, 3.5);

        stroke(0, 0, lineBrightness);

        var nextX, nextY;
        nextX = x + floor(random(-1,2)) * segmentLength;
        nextY = y + floor(random(-1,2)) * segmentLength;

        line(x, y, nextX, nextY);

        x = nextX;
        y = nextY;
    }
}

