class Line {
  constructor(angle, length, noiseOffset) {
    this.angle = angle;
    this.noiseScale = 3;
    this.length = length * 0.8 + noise(this.angle * this.noiseScale + noiseOffset) * length * 1.2;
    this.randomInnerAnlge = this.angle + random(-0.2, 0.2);

    this.beginPos = createVector(cos(this.randomInnerAnlge) * this.length * 0.2, sin(this.randomInnerAnlge) * this.length * 0.2);
    this.endPos = createVector(cos(this.angle) * this.length, sin(this.randomInnerAnlge) * this.length);
    this.dir = p5.Vector.sub(this.endPos, this.beginPos);

    this.curPos = this.beginPos.copy();
    //this.currentLength = 0;
    if (random(0, 1) < 0.4)
      this.alpha = random(15, 30);
    else
      this.alpha = map(length, 100, 300, 180, 20);

    this.dir.setMag(map(this.alpha, 15, 180, 7, 4) + random(-2, 2));


  }

  show() {
    // if (this.currentLength < this.length)
    //   this.currentLength += 0.1;
    push();
    translate(width / 2, height / 2);
    stroke(this.alpha);
    strokeWeight(1);
    //line(this.beginPos.x, this.beginPos.y, this.endPos.x, this.endPos.y);

    if (p5.Vector.sub(this.endPos, this.curPos).magSq() > 5)
      this.curPos = p5.Vector.add(this.curPos, this.dir);

    line(this.beginPos.x, this.beginPos.y, this.curPos.x, this.curPos.y);



    pop();

  }


}

