const canvas = {
    w: 800,
    h: 600
};

function setup() {
    createCanvas(canvas.w, canvas.h);
    strokeWeight(.2);
    colorMode(HSB, 1);
    stroke(.7, 0, 1);
    noFill();
    angleMode(DEGREES);
}

function draw() {
    background(0);
    //background(7, 2, 4, 5);
    makeShape(Math.floor(canvas.w *5));
}

function makeRing(radius) {

  //define shape
  beginShape();
    let numPoints = 4;
    for(let i=0; i < numPoints; i++) {
        let raw = {
            x: (canvas.w / 2) + radius * Math.cos(i * (2 * Math.PI) / numPoints),
            y: (canvas.h / 2) + radius * Math.sin(i * (2 * Math.PI) / numPoints)
        };
        let noisyRadius = radius * noise(raw.x * .01, raw.y * .01, millis() * .00005);

      //position
      let noisy = {
            x: (canvas.w / 2) + noisyRadius * Math.cos(i * (2 * Math.PI) / numPoints),
            y: (canvas.h / 2) + noisyRadius * Math.sin(i * (2 * Math.PI) / numPoints)
        };
        vertex(noisy.x, noisy.y);
    }
    endShape(CLOSE);
}

function makeShape(numRings) {
    for (let i = 0; i < numRings; i += 1) {
        makeRing(i);
    }
}

