function drawMoons() {
    var moonLocation = new Point(150, 150);
    for (var i = 0; i < 16; i++) {
        //set moon Location
        if (i > 3 && i % 4 == 0) {
            moonLocation.y += 150;
            moonLocation.x = 150;
            row.y += 400;
        } else {
            moonLocation.y = row.y;
        }


        var moon = new Path.Circle(moonLocation, moonRadius);
        moon.strokeColor = 'grey';
        if (i < halfWay) {

            var sunLocation = moonLocation + new Point(moonSunSeperation, 0);
            var sun = new Path.Circle(sunLocation, sunRadius);
            sun.strokeColor = 'grey';
            var moonPath = moon.subtract(sun);
            var group = makeNewRasterGroup(moonPath, moonLocation, moonSeperation, row);
            moonLocation += new Point(moonSeperation, row);

        } else if (i >= halfWay) {
            sunLocation.x = moonLocation.x - sunRadius + change;
            sunLocation.y = moonLocation.y;
            var sun = new Path.Circle(sunLocation, sunRadius);
            sun.strokeColor = 'grey';
            var moonPath = moon.intersect(sun);
            var group = makeNewRasterGroup(moonPath, moonLocation, moonSeperation, row);
            moonLocation += new Point(moonSeperation, row);


        } else if (i + 1 == (halfWay * 2)) {
            moon.strokeColor = 'blue';
            moonLocation += new Point(moonLocation, moonRadius);
        }
        if (i < halfWay) {
            moonSunSeperation += 34;
            sunRadius += 22;
        } else if (i >= halfWay) {
            change += 12;
            sunRadius -= 24;
        }
        moonPath.style = {
            fillColor: null,
            shadowBlur: 12,
            shadowOffset: new Point(5, 5),
        };


    }
}

function makeNewRasterGroup(moonPath, moonLocation, moonSeperation, row) {
    var raster = new Raster('moon', moonLocation);
    raster.size = 250;
    var group = new Group([moonPath, raster]);
    group.clipped = true;
    // group.style = {
    //     shadowBlur: 12,
    //     shadowOffset: new Point(5, 5),
    //     strokeColor: 'purple',
    // }
    return group;
}

