const cmajor = [
    'C2', 'D2', 'E2', 'F2', 'G2', 'A2', 'B2',
    'C3', 'D3', 'E3', 'F3', 'G3', 'A3', 'B3',
    'C4', 'D4', 'E4', 'F4', 'G4', 'A4', 'B4',
];
const notes = cmajor;

let degree = 0;

function generate() {
  // choose a starting place
  degree = randomInt(0, 7);

  // generate some measures
  const a = generateMeasure('rise');
  const b = generateMeasure('fall');

  return [].concat(a, b, a, b);
}

function generateMeasure(riseOrFall) {
  m = [];

  let timeLeft = Tone.Time('1m');

  while (timeLeft.toSeconds() > 0) {
    // choose note
    // heavily biased towards climb
    let change;
    if (riseOrFall === 'rise') {
        change = sample([1, 1, -1, 1, 1, 1, 2, 2, -2, 3, 3]);
    } else {
        change = sample([-1, -1, 1, -1, -1, -1, -2, -2, 2, -3, -3]);
    }

    degree = constrain(degree + change, 0, cmajor.length);
    const note = notes[degree];

    // choose length
    let length = Tone.Time('16n');
    if (Math.random() < 0.5) {
      length = Tone.Time('8n');
    }
    if (length.toMilliseconds() > timeLeft.toMilliseconds()) {
      length = timeLeft;
    }

    // keep track of time
    timeLeft = Tone.Time(timeLeft - length);

    // add the note to the melody
    m.push([note, length]);
  }

  return m;
}



function play(melody) {
  console.log("---- start ----");
  let t = Tone.now();
  for (const note of melody) {
    console.log(note[0], note[1].toNotation());
    if (note[0] !== 'rest') {
      // synth.triggerAttackRelease(note[0], note[1]), t);
      synth.triggerAttackRelease(note[0], Tone.Time(note[1]) - 0.1, t);
    }
    t += Tone.Time(note[1]);
  }
  console.log("---- stop -----");
}


