function drawBall(x, y, r, myfill) {
  push();
  translate(x, y);
  // shadow
  if (shadows) {
    push();
    fill(shadingColor);
    ellipse(0, r, 2 * r, 0.1 * r);
    pop();
  }
  // base circle
  fill(myfill);
  circle(0, 0, r);
  drawReflections(0, 0, r);
  drawProperArc(r, r * 0.25, 60, shadingColor);
  drawProperArc(r * 0.9, r * 0.1, -120, highlightColor);
  pop();
}

function drawReflections(x, y, r) {
  push();
  fill(highlightColor);
  circle(x - 0.3 * r, y - 0.4 * r, 0.08 * r);
  circle(x - 0.44 * r, y - 0.24 * r, 0.04 * r);
  // circle(x - 0.3 * r, y + 0.5 * r, 0.04 * r);
  pop();
}

function drawProperArc(radius, progress, rotation, myfill) {
  push();
  rotate(rotation);
  fill(myfill);
  beginShape();
  // Start, mid bottom
  vertex(0, radius);
  // Outer (left)
  bezierVertex(
    +radius * myB,
    radius,
    +radius,
    0 + (1 - myB * 0.98) * radius,
    +radius,
    0
  );
  bezierVertex(
    +radius,
    -radius * myB,
    0 + (1 - myB) * radius * 0.98,
    -radius,
    0,
    -radius
  );
  // Inner (right)
  bezierVertex(
    (+radius - progress) * myB,
    -radius + (1 - myB * 0.98) * radius,
    +radius - progress,
    0 - (1 - myB * 0.98) * radius,
    0 + radius - progress,
    0
  );
  bezierVertex(
    (+radius - progress) * myB,
    radius - (1 - myB * 0.98) * radius,
    0,
    radius,
    0,
    radius
  );
  endShape();
  pop();
}

