//observation: 
//1. color 
//2. size: circles are in different size 
//3. circles' positions are on the center 

function setup() {
    createCanvas(400, 400);
}


function draw() {
    background(50);
    noStroke();
    ellipseMode(CENTER);




    var frequency = 0.5;
    for (var i = 0; i < 100; i++) {
        // these points are not scattered in the same way
        // how can you make the arrangement match the challenge?
        //- noise placement 
        var x = noise(i * frequency, 0) * width;
        var y = noise(i * frequency, 1000) * height;

        // the diameter shouldn't always be 10, it needs to vary
        //- middle bias distribution 
        var diameter = (random(5,20)+ random(10,20))/2;

        // the colors also need to change
        // what colorMode would be easiest to work with?
        if(diameter >15){
        fill(random(0,255), random(0,100), random(200,255));
        }else if(diameter>10 && diameter <15){
            fill(random(0,100), max(random(0,250),random(230,250)), random(100,255));
        }else  {
            fill(random(220,255),random(50,100),random(0,50));
        }


        ellipse(x, y, diameter, diameter);
    }


    noLoop();


}

