// require https://cdnjs.cloudflare.com/ajax/libs/p5.js/0.5.14/p5.js
// require /turtles/turtle/turtle.js

let myTurtle;

let turnOptions = [];
let forwardOptions = [];

const stepDist = 1;
const turnDeg = 90;
const strokeWt = .6;

let hexRed;
let hexYellow;
let hexBlue;

function setup() {
    createCanvas(windowWidth, windowHeight);
    myTurtle = new Turtle();

    noFill();
    strokeWeight(strokeWt);
    background(245, 245, 245);

    for (let i = 0; i < 360; i += turnDeg) {
        turnOptions.push(i);
    }

    hexRed = color("#e0233d");
    hexYellow = color("#e0b023");
    hexBlue = color("#4286f4");

    chooseRandColor();
}

function draw() {
    // teleport to center of canvas
    myTurtle.moveTo(width / 2, height / 2);
    myTurtle.penDown();

    // put the pen down to draw
    for (let i = 0; i < 8000; i++) {

        let nX = noise(myTurtle.x * .01, 0, 0);
        let nY = noise(0, myTurtle.y * .01, 1000);
        let thisTurnIndex = Math.floor(random(turnOptions.length));
        let thisTurn = turnOptions[thisTurnIndex];

        if (myTurtle.x < width && myTurtle.x > 0) {
            if (myTurtle.y < height && myTurtle.y > 0) {
                myTurtle.moveForward(stepDist);
                myTurtle.turnRight(thisTurn + ((random(-1, 1) * nY) / 10));
            }
        }

        if (thisTurn === 90) {
            chooseRandColor();
        }

        push();
        for (let i = 0; i < 10; i++) {
            let thisTurn = turnOptions[thisTurnIndex];
            if (myTurtle.x < width && myTurtle.x > 0) {
                if (myTurtle.y < height && myTurtle.y > 0) {
                    myTurtle.moveForward(stepDist);
                    myTurtle.turnRight(thisTurn);
                }
            }
        }
        pop();
    }
	noLoop();
}

function chooseRandColor() {
    let randC = random();
    if (randC < (1/3)) {
        stroke(color(hexRed));
        fill(color(hexRed));
    } else if (randC < (2/3)) {
        stroke(color(hexYellow));
        fill(color(hexYellow));
    } else if (randC < 1) {
        stroke(color(hexBlue));
        fill(color(hexBlue));
    }
}

