function generate() {

    const songGrammar = {
        "song": ["#g# #measure1# #rest#"],
        "measure1": ["#p1# #p1# #p1# #p1# #p1# #p1# #p1# #p1#"],
        "p1": ['#c# #D/e# #fg#'],
        "fg": ['#f# #g#'],
        "D/e": ['#D#', '#e#'],
        "g": ["g4 2n"],
        "c": ["c4 2n"],
        "f": ["f4 8n"],
        "D": ["D4 4n"],
        "e": ["e4 4n"],
        "rest": ["REST 1m"],

    }
    let grammar = tracery.createGrammar(songGrammar);
    console.log(grammar);
    let song = grammar.flatten("#song#");
    // console.log(song);
    const storyDiv = document.createElement('div');
    storyDiv.style = "font-size: 30px; margin: 10%; line-height: 1.5;";
    storyDiv.textContent = song;

    document.body.insertAdjacentElement("beforeend", storyDiv);
    return song;
}


function play(melody) {
    let t = Tone.now();
    for (let i = 0; i < melody.length; i += 2) {
        // console.log(melody[i], melody[i + 1].toNotation());
        console.log(melody[i]); 
        if (melody[i] !== 'REST') {
            // synth.triggerAttackRelease(note[0], note[1]), t);
            synth.triggerAttackRelease(melody[i], Tone.Time(melody[i + 1]) - 0.01, t);
        }
        t += Tone.Time(melody[i+1]);
    }
}

