// Talker class
class Talker {
    constructor(_x, _y, _phrase_key) {
        this.origin = {
            x: _x,
            y: _y
        },
        this.x = _x,
        this.y = _y,
        this.phrase_key = _phrase_key,
        this.diam = 14,
        // this.disperseValenceX = Math.floor(random(-1, 2)),
        // this.disperseValenceY = Math.floor(random(-1, 2)),
        this.disperseNoiseX = noise(this.x * .01, 0),
        this.disperseNoiseY = noise(this.y * .01, 1)
    }

    convene() {
        this.x = lerp(this.x + random(-.3, .3), meetingPoint.x, .003);
        this.y = lerp(this.y + random(-.3, .3), meetingPoint.y, .003);
    }

    disperse() {
        this.x = lerp(this.x + random(-.3, .3), this.origin.x, .004);
        this.y = lerp(this.y + random(-.3, .3), this.origin.y, .004);
    }

    show() {
        noStroke();
        fill(50);
        ellipse(this.x, this.y, this.diam, this.diam);
    }

    talk() {
        let distToMeetingPt = dist(this.x, this.y, meetingPoint.x, meetingPoint.y);
        if (
            distToMeetingPt < triggerDist &&
            phrases[this.phrase_key].isPlaying() === false &&
            convene === true
        ) { 
            phrases[this.phrase_key].setVolume(1, 3);
            phrases[this.phrase_key].play();
        }
    }

    quiet() {
        phrases[this.phrase_key].setVolume(0, 2);
        // phrases[this.phrase_key].stop();
    }
}

