//get quick function by Justin Bakse
function getQuick(img, x, y) {
    var i = (y * img.width + x) * 4;
    return [
        textImage.pixels[i],
        textImage.pixels[i + 1],
        textImage.pixels[i + 2],
        textImage.pixels[i + 3],
    ];
}

