// Depends on local data sheets to run

vowel_datasheet = './data/vowels.txt';
consonant_datasheet = './data/consonants.txt';

function readDatasheet(file) {
    const raw_file = new XMLHttpRequest();
    let file_contents = '';
    raw_file.open("GET", file, false);
    raw_file.onreadystatechange = function () {
        if(raw_file.readyState === 4) {
            if(raw_file.status === 200 || raw_file.status == 0) {
                file_contents = raw_file.responseText;
            }
        }
    }
    raw_file.send(null);
    return file_contents;
}

function parseDatasheet(type) {
    const data = [];
    const datasheet = `./data/${type}.txt`;
    const lines = readDatasheet(datasheet).split('\n');

    // Create phoneme objects
    for (let i = 0; i < lines.length; i++) {
        line = lines[i].split(',');
        if (type === 'vowels') {
            data[i] = {
                symbol: `${line[0]}`,
                is_monophthong: line[1],
                is_schwa: line[2]
            }
        } else (
            data[i] = {
                symbol: `${line[0]}`,
                manner: line[1],
                place: line[2],
                is_voiced: line[3]
            }
        )
    }

    return data;
}

