function createSyllable() {
    rand = getRand(1);
    let onset = '';
    let nucleus = vowels[Math.floor(getRand(vowels.length))].symbol;
    let coda = '';

    if (rand < .3) {
        onset = consonants[Math.floor(getRand(consonants.length))].symbol;
    } else if (rand < .5) {
        onset = consonants[Math.floor(getRand(consonants.length))].symbol +
                consonants[Math.floor(getRand(consonants.length))].symbol;
    } else {
        onset = consonants[Math.floor(getRand(consonants.length))].symbol;
        coda = consonants[Math.floor(getRand(consonants.length))].symbol;
    }

    return `${onset} ${nucleus} ${coda}`.trim();
}

