function setup() {
    createCanvas(canvasWidth, canvasHeight);
    drawInputControls();
    noFill();

    // defaults
    rectMode(CENTER);
}

function draw() {
    background(255);

    // if we're reading a value from setColor, then use it
    if (setColor()) {
        fill(setColor())
    } else {
        fill(255);
    }

    // if we're reading a value for size, then use it
    if (setSize()) {
        if (
            setSize() === "gigantic" ||
            setSize() === "huuuge"
        ) {
            shapeSize = 400;
        } else if (
            setSize() === "huge" ||
            setSize() === "bigger"
        ) {
            shapeSize = 300;
        } else if (
            setSize() === "big"
        ) {
            shapeSize = 200;
        } else if (
            setSize() === "medium"
        ) {
            shapeSize = 100;
        } else if (
            setSize() === "small"
        ) {
            shapeSize = 50;
        } else if (
            setSize() === "tiny" ||
            setSize() === "little"
        ) {
            shapeSize = 10;
        }
    }

    // if we're reading a value from setxPos, then use it
    if (setXPos()) {
        if (setXPos() === "left") {
            shapePos.x = shapeSize / 2;
        } else if (setXPos() === "right") {
            shapePos.x = canvasWidth - shapeSize / 2 - 2; // hacky -2
        } else if (
            setXPos() === "middle" ||
            setXPos() === "center"
        ) {
            shapePos.x = canvasWidth / 2, canvasHeight / 2;
        }
    }

    // if we're reading a value from setyPos, then use it
    if (setYPos()) {
        if (
            setYPos() === "top" ||
            setYPos() === "up"
            ) {
            shapePos.y = shapeSize / 2;
        } else if (
            setYPos() === "bottom" ||
            setYPos() === "down"
            ) {
            shapePos.y = canvasHeight - (shapeSize / 2); // hacky -2
        }
    }

    // if we're reading a value from setShape, then use it
    if (setShape()) {
        // make the shape
        if (
            setShape() === "rect" ||
            setShape() === "rectangle" ||
            setShape() === "square" ||
            setShape() === "box"
        ) {
            rect(shapePos.x, shapePos.y, shapeSize, shapeSize);
        } else if (
            setShape() === "circle" ||
            setShape() === "ellipse"
        ) {
            ellipse(shapePos.x, shapePos.y, shapeSize, shapeSize);
        } else if (
            setShape() === "triangle"
        ) {
            triangle(
                shapePos.x, shapePos.y - shapeSize / 2,
                shapePos.x - shapeSize / 2, shapePos.y + shapeSize / 2,
                shapePos.x + shapeSize / 2, shapePos.y + shapeSize / 2,
            );
        }
    }
}


