function setColor() {
    let string = input.value().toLowerCase(); // store text input from user
    let colorSubstring;

    colors = [ // all colors this program recognizes
        /red/,
        /orange/,
        /yellow/,
        /green/,
        /blue/,
        /purple/,
        /pink/,
        /black/,
        /brown/,
        /teal/,
        /maroon/,
        /white/,
        /gray/, /grey/
    ];

    // check if the string contains any color keywords
    for (i = 0; i < colors.length; i++) {
        let regex = colors[i];

        // if a match is found
        if (regex.test(string) === true) {
            match = string.match(regex);
            colorSubstring = match[0];
            // fill(colorSubstring);
        };
