// code from Point Placing Demo activity in class http://compform.net/strategy/

var POINT_COUNT = 500;
var SHAPE_SIZE = 40;

function setup() {
    createCanvas(500,500);
    noStroke();
    fill(250);
    rectMode(CENTER);
}

function draw() {

    background(255, 58, 58);
    points = noiseCull(placePointsGrid(sqrt(POINT_COUNT), width, height), .4, .3, .3);
    drawPoints(points);

    noLoop();

}

function drawPoints(points) {
    for (var i = 0; i < points.length; i++) {
        var x = points[i].x;
        var y = points[i].y;

        rect(x, y, SHAPE_SIZE, SHAPE_SIZE);
    }
}

function noiseCull(inPoints, frequency, threshold, dither) {
    var outPoints = [];

    for (var i = 0; i < inPoints.length; i++) {
        var p = inPoints[i];

        if (noise(p.x * frequency, p.y * frequency) < threshold + random() * dither) {
            outPoints.push(p);
        }
    }

    return outPoints;
}

function placePointsGrid(grid_size, w, h) {
    var points = [];

    for (var y = 0; y < grid_size; y++) {
        for (var x = 0; x < grid_size; x++) {
            points.push({
                x: (x + .5) / grid_size * w,
                y: (y + .5) / grid_size * h,
            });
        }
    }

    return points;
}

